mapper.update:
update member
set name=#{name}, pwd=#{pwd}, address1=#{address1}, address2=#{address2}, zipCode=#{zipCode}, 
fileName=#{fileName}, fileSize=#{fileSize}, originalFileName=#{originalFileName}
where email=#{email}

delete from member 
where email=#{email}
and pwd=#{pwd}

update member
set pwd=#{pwd}
where email=#{email}
