mapper.select:
select * from
(
	select rownum as RNUM, A.*
		from(
			select * from qna
			where member_no=#{memNo}
			order by qna_no desc
		)A
)

where RNUM>#{firstRecordIndex} 
	and RNUM<=#{firstRecordIndex}+ #{recordCountPerPage}

mapper.update:
update qna
set title=#{title}, content=#{content}
where qna_no=#{qnaNo}
